    not_circulating_vesting = Web3.to_checksum_address('0xB3807b9E5A98fc203C684aa2227813fdB733955A')
    not_circulating_gnosis = Web3.to_checksum_address('0xc97256f93b9ed9edb226c51e6c00ea2e9419779e')
    contract = w3.eth.contract(contract_address, abi=ABI)
    decimals = contract.functions.decimals().call()
    DECIMALS = 10 ** decimals
    not_circulating_vesting_balance = contract.functions.balanceOf(not_circulating_vesting).call() // DECIMALS
    not_circulating_gnosis_balance = contract.functions.balanceOf(not_circulating_gnosis).call() // DECIMALS
    print("Vesting Balance " + str(not_circulating_vesting_balance))
    print("Gnosis Balance " + str(not_circulating_gnosis_balance))
    total_supply = contract.functions.totalSupply().call() // DECIMALS
    print("Total Supply " + str(total_supply))
    circulating_supply = int(total_supply) - int(not_circulating_vesting_balance)
    print("Circulating Supply " + str(circulating_supply))
    return str(circulating_supply)



